import React from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';

import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'

import './styles.css'

const inputStyle = {
    width: '70%',
    marginLeft: '15%',
    marginTop: '2vh',
    padding: '0.5%'
}
const buttonStyle = {
    width: '20%',
    height: '7vh',
    marginLeft: '15.5%',
    marginTop: '2vh',
    fontSize: '150%'
}

const wshost = window.location.origin.replace(/^http/, 'ws')

var socket = new WebSocket('ws://localhost:8000');

console.log(window.location.origin.replace(/^http/, 'ws'))

function App() {

    const [value, setValue] = React.useState('')
    const [slug, setSlug] = React.useState('')

    function handleChange(e) {
        setValue(e.target.value)
    }
    function onClick() {
        socket.send(value)
    }

    socket.onmessage = function(data) {
        setSlug(data)
    }

    return (
        <div>
            <h1>URL Shortener</h1>
            <TextField className='input' label="Enter URL:" variant="filled" style={inputStyle} inputProps={{style: {fontSize: '200%'}}} InputLabelProps={{style: {fontSize: '150%', padding: '0.5%'}}} onChange={(e) => handleChange(e)} />
            <Button variant="contained" style={buttonStyle} onClick={onClick}>
                Primary
            </Button>
            <h1>your url is https://torbet.co/{slug}</h1>
        </div>
    )
}

ReactDOM.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>,
    document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
